import React from 'react';
import { Redirect, Route } from 'react-router';

const StudentRoute = ({ children, ...rest }) => {
    const user=JSON.parse(localStorage.getItem("user"))
   

    return (
        <div>
        <Route
  {...rest}
  render={({ location }) =>
    user && user.role==="Student" ? (
      children
    ) : (
      <Redirect
        to={{
          pathname: "/login",
          state: { from: location }
        }}
      />
    )
  }
/>
    </div>
    );
};

export default StudentRoute;
